const key = "RGAPI-84d33f36-f096-44d2-baea-b62b26af9979";
const proxyUrl = "https://thingproxy.freeboard.io/fetch/";

async function fetchJSON(url) {
    const data = await fetch (url);
    return await data.json();
}

//to prevent too many requests to the riot api, save json
var currentSummonerJSON = null;

async function requestSummonerJSON(player) {
    const riot_api_url = `https://euw1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${player}?api_key=${key}`;
    const full_url = `${proxyUrl}${riot_api_url}`;
    const json = await fetchJSON(full_url);
    return json;
}

async function getSummonerJSON(player) {
    if (currentSummonerJSON === null || currentSummonerJSON.name !== player) {
        //new request
        currentSummonerJSON = requestSummonerJSON(player);
        console.log("yes");
    }
    return currentSummonerJSON;
}

async function getProfileIconId(player) {
    const json = await getSummonerJSON(player);
    return json.profileIconId;
}

export async function getPlayerIconPNGLink(player) {
    const iconId = await getProfileIconId(player);
    return `http://ddragon.leagueoflegends.com/cdn/10.15.1/img/profileicon/${iconId}.png`;
}

export async function getPlayerLevel(player) {
    const json = await getSummonerJSON(player);
    return json.summonerLevel;
}
