/**
 * League player profile for Myslibros
 */
function LeagueProfile(props) {
    const [player, setPlayer] = useState({
        "summonerInfo":{"id":"","accountId":"","puuid":"","name":"","profileIconId":0,"revisionDate":0,"summonerLevel":0},
        "leagueInfo":[
            {"leagueId":"","queueType":"","tier":"default","rank":"","summonerId":"","summonerName":"","leaguePoints":0,"wins":0,"losses":0,
            "veteran":false,"inactive":false,"freshBlood":false,"hotStreak":false},
            {"leagueId":"","queueType":"","tier":"default","rank":"","summonerId":"","summonerName":"","leaguePoints":0,"wins":0,"losses":0,
            "veteran":false,"inactive":false,"freshBlood":false,"hotStreak":false}
        ]
    });

    const getRequest = async (url) => {
        const data = fetch(url)
        .then(res => res.json());
        return data;
    }
    
    useEffect(() => {
        fetchPlayer();
    }, []);
    
    const fetchPlayer = async () => {
        let get_url = "http://localhost:3001/api/leagueoflegends/players/" + props.leagueName;
        const data = await getRequest(get_url);
        setPlayer(data);
        console.log(player);
        console.log(data);
    }
        

