const { default: LeaguePlayerDetails } = require("./LeaguePlayerDetails");

class LeagueOfLegendsApi {
    constructor() {
        this.key = "RGAPI-84d33f36-f096-44d2-baea-b62b26af9979";
        this.proxyUrl = "https://thingproxy.freeboard.io/fetch/";
    }

    fetchJSON(url) {
        const data = fetch(url);
        return data.json();
    }

    getProfileIconId(player) {
        riot_api_url = `https://euw1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${player}?api_key=${this.key}`;
        full_url = `${this.proxyUrl}${riot_api_url}`;
        const json = fetchJSON(full_url);
        return json.profileIconId;
    }


}

module.exports = LeaguePlayerDetails;
