import { raw } from 'body-parser';
//TODO: own css for myslibros page

import React, { useEffect, useState } from 'react';
import '../../App.css';
//import {getPlayerIconPNGLink, getPlayerLevel, getFlexRank, get_Solo_Duo_Rank} from './LeagueOfLegendsApi.js';

/**
 * League player profile for Myslibros
 */
function LeagueProfile(props) {
    const [player, setPlayer] = useState({});
    
    const getRequest = async (url) => {
        const data = fetch(url)
        .then(res => res.json());
        return data;
    }
    
    useEffect(() => {
        fecthPlayer();
    }, []);
        
    const getArabicNumber = async (romanNumber) => {
        switch (romanNumber) {
            case "IV":
                return "4";
            case "III":
                return "3";
            case "II":
                return "2";
            case "I":
                return "1";
            default:
                return "unknown";
        }
    }

    const getRank = async (leagueInfo, rankType) => {
        for (let i = 0; i < leagueInfo.length; i++) {
            if (leagueInfo[i].queueType === rankType) {
                const arabicNumber = await getArabicNumber(leagueInfo[i].rank);
                return `${leagueInfo[i].tier.toLowerCase()}_${arabicNumber}`;
            }
        }
        return "default";
    }

    const fecthPlayer = async () => {
        const fetchedPlayer = await fetch("http://localhost:3001/api/leagueoflegends/players/" + props.leagueName).then(res => res.json());
        var player = {};
        player.leagueName = fetchedPlayer.summonerInfo.name;
        player.level = fetchedPlayer.summonerInfo.summonerLevel;
        player.IconIdSrc = `//opgg-static.akamaized.net/images/profile_icons/profileIcon${fetchedPlayer.summonerInfo.profileIconId}.jpg?image=q_auto&v=1518361200`;
        player.soloDuoRank = await getRank(fetchedPlayer.leagueInfo, "RANKED_SOLO_5x5");
        player.flexRank = await getRank(fetchedPlayer.leagueInfo, "RANKED_FLEX_SR");
        setPlayer(JSON.parse(JSON.stringify(player)));
    }
        

    /*const fetchPlayers = async () => {
        var json = {};
        json.name = props.leagueName;
        json.level = fetch('/api/leagueoflegends/players')
            .then(res => res.json())
            .then(players => ) 
            
            
            //await getPlayerLevel(props.leagueName);
        json.iconSrc = await getPlayerIconPNGLink(props.leagueName);
        json.flexRank = await getFlexRank(props.leagueName);
        json.soloDuoRank = await get_Solo_Duo_Rank(props.leagueName);
        //json.lane = await getOverallLane(props.leagueName);
        const player = JSON.parse(JSON.stringify(json));
        setPlayer(player);
      };*/
    
    return (
        <div className="league-profile">
            <div className="league-profile-header">
                <div className="league-profile-header-picture-container">
                    <img className="league-profile-picture" src={player.IconIdSrc} alt=""></img>
                </div>
                <div className="league-profile-header-text">
                    <h1>{player.leagueName}</h1>
                    <h1>Level: {player.level}</h1>
                </div>
            </div>
            <div className="league-info">
                {
                    //img for solo duo rank: `opgg-static.akamaized.net/images/medals/${player.soloDuoRank}.png?image=q_auto&v=1`
                    //img for flex: `opgg-static.akamaized.net/images/medals/${player.flexRank}.png?image=q_auto&v=1`
                }
                {/*<h1>{player.soloDuoRank}</h1>
                <h1>{player.flexRank}</h1>*/}
            </div>
        </div>
    );
}

export default LeagueProfile;
