        playerJSON.level = data.summonerInfo.summonerLevel;
        playerJSON.iconSrc = `opgg-static.akamaized.net/images/profile_icons/profileIcon${data.summonerInfo.profileIconId}.jpg?image=q_auto&v=1518361200`;
