async function getLane(matchJSON) {
    const lane = matchJSON.lane;
    const role = matchJSON.role;

//    Role: DUO, DUO_CARRY, DUO_SUPPORT, NONE, and SOLO
//    Lane: MID, TOP, BOTTOM, JUNGLE, NONE

    switch(lane) {
        case "MID":
            switch(role) {
                case "SOLO":
                    return "mid";
                default:
                    return "none";
            }
        case "TOP":
            switch(role) {
