        case "JUNGLE":
            switch(role) {
                case "NONE":
                    return "jgl";
                default:
                    return "none";
            }
        case "BOTTOM":
            switch(role) {
                case "DUO_SUPPORT":
                    return "bot_sup";
                case "DUO_CARRY":
                    return "bot_adc";
                default:
                    return "none";
            }
        default:
            return "none";
    }

}

