import { raw } from 'body-parser';
//TODO: own css for myslibros page

import React, { useEffect, useState } from 'react';
import '../../App.css';
//import {getPlayerIconPNGLink, getPlayerLevel, getFlexRank, get_Solo_Duo_Rank} from './LeagueOfLegendsApi.js';

/**
 * League player profile for Myslibros
 */
function LeagueProfile(props) {
    const [playerState, setPlayerState] = useState({});
    
    const getRequest = async (url) => {
        const data = fetch(url)
        .then(res => res.json());
        return data;
    }
    
    useEffect(() => {
        setPlayer();
    }, []);
        
    const setPlayer = async () => {
        var json = {};
        const fetchedPlayer = await fetch("http://localhost:3001/api/leagueoflegends/players/" + props.leagueName)
                            .then(res => res.json());
        json.leagueName = fetchedPlayer.summonerInfo.name;
        json.level = fetchedPlayer.summonerInfo.summonerLevel;
        const fullJson = JSON.parse(JSON.stringify(json));
        setPlayerState(fullJson);
    }
        

    /*const fetchPlayers = async () => {
        var json = {};
        json.name = props.leagueName;
        json.level = fetch('/api/leagueoflegends/players')
            .then(res => res.json())
            .then(players => ) 
            
            
            //await getPlayerLevel(props.leagueName);
        json.iconSrc = await getPlayerIconPNGLink(props.leagueName);
        json.flexRank = await getFlexRank(props.leagueName);
        json.soloDuoRank = await get_Solo_Duo_Rank(props.leagueName);
        //json.lane = await getOverallLane(props.leagueName);
        const player = JSON.parse(JSON.stringify(json));
        setPlayer(player);
      };*/
    
    return (
        <div className="league-profile">
            <div className="league-profile-header">
                <div className="league-profile-header-picture-container">
                    {/*<img className="league-profile-picture" src={player.iconSrc} alt=""></img>*/}
                </div>
                <div className="league-profile-header-text">
                    <h1>{playerState.leagueName}</h1>
                    <h1>Level: {playerState.level}</h1>
                </div>
            </div>
            <div className="league-info">
                {
                    //img for solo duo rank: `opgg-static.akamaized.net/images/medals/${player.soloDuoRank}.png?image=q_auto&v=1`
                    //img for flex: `opgg-static.akamaized.net/images/medals/${player.flexRank}.png?image=q_auto&v=1`
                }
                {/*<h1>{player.soloDuoRank}</h1>
                <h1>{player.flexRank}</h1>*/}
            </div>
        </div>
    );
}

export default LeagueProfile;
